import React, { Component } from 'react';

import '../../styles/css/fightZone/ModaleInstructions.css';
import rouleau from '../../assets/rouleau.png';

class ModaleInstructions extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modaleIsOpen: false
    };
    this.closeModal = this.closeModal.bind(this);
  }

  closeModal = () => {
    this.setState({ modaleIsOpen: !this.state.modaleIsOpen });
  };

  render() {
    return (
      <div className={this.state.modaleIsOpen ? 'modale_close' : 'modale_open'}>
        <img className="rouleau" src={rouleau} alt="" />
        <span className="instructions">
          <div className="top">
            <span class={this.state.modaleIsOpen ? 'modale_close' : 'arrow'}>
              <span class="mask1"></span>
              <span class="mask2"></span>
              <span class="mask3"></span>
            </span>
            <h2>How to play :</h2>
          </div>
          <ul>
            <p>Both player have to click on their own tap</p>
            <p>That's all !!</p>
          </ul>
          <button onClick={() => this.closeModal()}>Play now !!!</button>
        </span>
      </div>
    );
  }
}

export default ModaleInstructions;

