class Countdown extends React.Component {
  constructor(props) {
    super(props);
    this.state = { counter: props.val };
  }

  render() {
    var x = this;
    var { counter } = this.state;
    setTimeout(function() {
      if (counter > 0) {
        x.setState({ counter: counter - 1 });
      }
    }, 1000);
    return <div className="count-button">{counter}</div>;
  }
}

export default Countdown;

