import React from 'react';

import '../../styles/css/fightZone/FightZone.css';
// import Tap from './Tap';
import DisplayCountdown from './DisplayCountdown';
import ModaleInstructions from './ModaleInstructions';
import KeyboardEventHandler from 'react-keyboard-event-handler';

class FightZone extends React.Component {
  constructor() {
    super();
    this.state = {
      modaleIsOpen: false,
      counter: 20,
      winP1: false,
      winP2: false,
      kick: null,
      punch: null,
      PV1: 1000,
      PV2: 1000
    };

    this.handleCloseModal = this.handleCloseModal.bind(this);
    this.handleSetTimeOut = this.handleSetTimeOut.bind(this);
    // this.CoupDePied = this.CoupDePied.bind(this);
  }

  componentDidUpdate() {
    const { winP1, counter } = this.state;
    if (counter === 0 && !winP1) {
      this.handleWinnerLeft();
    }
  }

  handleWinnerLeft = () => {
    this.setState({ winP1: true });
  };

  handleWinnerRight = () => {
    this.setState({ winP2: true });
  };

  handleCloseModal = () => {
    this.setState({ modaleIsOpen: !this.state.modaleIsOpen });
  };

  handleSetTimeOut = () => {
    const x = this;
    const { counter } = this.state;
    setTimeout(function() {
      if (counter > 0 && counter !== 0) {
        x.setState({ counter: counter - 1 });
      }
    }, 1000);
  };

  handleCombat = key => {
    if (key === 's') {
      this.CoupDePied1();
      this.setState({ PV2: Math.round(this.state.PV2 - this.state.kick) });
    }
    if (key === 'z') {
      this.CoupDePoing1();
      this.setState({ PV2: Math.round(this.state.PV2 - this.state.punch) });
    }
    if (key === 'o') {
      this.CoupDePoing2();
      this.setState({ PV1: Math.round(this.state.PV1 - this.state.punch) });
    }
    if (key === 'k') {
      this.CoupDePied2();
      this.setState({ PV1: Math.round(this.state.PV1 - this.state.kick) });
    }
  };

  CoupDePied1 = () => {
    const pied1 = localStorage.getItem('strengthP1');
    const def2 = localStorage.getItem('defP2');
    this.setState({ kick: 10 * (1 + pied1 / 100) * ((100 - def2 / 2) / 100) });
  };

  CoupDePoing1 = () => {
    const poing1 = localStorage.getItem('strengthP1');
    const def2 = localStorage.getItem('defP2');
    this.setState({ punch: 5 * (1 + poing1 / 100) * ((100 - def2 / 2) / 100) });
  };

  CoupDePied2 = () => {
    const pied2 = localStorage.getItem('strengthP2');
    const def1 = localStorage.getItem('defP1');
    this.setState({ kick: 10 * (1 + pied2 / 100) * ((100 - def1 / 2) / 100) });
  };

  CoupDePoing2 = () => {
    const poing2 = localStorage.getItem('strengthP2');
    const def1 = localStorage.getItem('defP1');
    this.setState({ punch: 5 * (1 + poing2 / 100) * ((100 - def1 / 2) / 100) });
  };

  render() {
    console.log(this.state.PV1, this.state.PV2);
    const { modaleIsOpen, counter, winP1 } = this.state;
    const P2 = localStorage.getItem('avatarP2');
    const P1 = localStorage.getItem('avatarP1');

    return (
      <div className="mapFond">
        <div id={winP1 ? 'position1Win' : 'position2'}>
          <div className="winnerIs">
            <p>YOU WIN</p>
          </div>
        </div>
        <img className="fightMap" src={localStorage.getItem('flag')} alt="Zone de Combat" />

        <DisplayCountdown
          counter={counter}
          setTimeOut={this.handleSetTimeOut}
          modaleIsOpen={modaleIsOpen}
        />

        <ModaleInstructions closeModal={this.handleCloseModal} modaleIsOpen={modaleIsOpen} />

        <div className="fighters">
          <div className="leftPlayer">
            {/* <Tap /> */}
            <img className="fuck" src={P1} alt="Figther 1" />
          </div>

          <div className="rightPlayer">
            {/* <Tap /> */}
            <img
              className="fighter2"
              src={P2}
              alt="Figther 2"
              style={{ transform: 'scaleX(-1)' }}
            />
          </div>
        </div>
        <KeyboardEventHandler
          handleKeys={['a', 'z', 's', 'k', 'o', 'p']}
          onKeyEvent={(key, e) => this.handleCombat(key)}
        />
      </div>
    );
  }
}

export default FightZone;
