import React, { Component } from 'react';
import CheatPlayer from './CheatPlayer';

class PlayerInfos extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cheatCount: 0,

      name: 'Oups',
      strength: 80,
      intel: 50,
      avatar: 'https://pbs.twimg.com/media/C8SFjSbXgAAKoZx.jpg:large'
    };
  }

  onClickHandler = () => {
    this.setState({
      cheatCount: this.state.cheatCount + 1
    });
  };

  renderMiniDefault() {
    return (
      <div className="miniChars" id="miniChars2">
        <img className="miniPic" src="https://via.placeholder.com/150" alt="placeholder" />
        <img className="miniPic" src="https://via.placeholder.com/150" alt="placeholder" />
        <img className="miniPic" src="https://via.placeholder.com/150" alt="placeholder" />
        <img className="miniPic" src="https://via.placeholder.com/150" alt="placeholder" />
      </div>
    );
  }

  renderMiniCheat() {
    return (
      <div className="miniChars" id="miniChars2">
        <CheatPlayer />
      </div>
    );
  }

  renderMiniChars() {
    let clickAvatar = this.state.cheatCount;
    if (clickAvatar !== 0 && clickAvatar % 5 === 0) {
      return this.renderMiniCheat();
    } else {
      return this.renderMiniDefault();
    }
  }

  render() {
    return (
      <div>
        <div className="selectedChar">
          <img
            src={this.state.avatar}
            className="charPics"
            onClick={() => this.onClickHandler()}
            alt="Char1 selected"
            id="picChar1"
          />
          <div className="charInfos">
            <p className="nameChar" id="nameChar1">
              {this.state.name}
            </p>
            <p>Strength</p>
            <div className="emptyStatBar">
              <div
                className="barAnim"
                id="strengthBarChar1"
                style={{ width: `${this.state.strength}%` }}
              />
            </div>
            <p>Intelligence</p>
            <div className="emptyStatBar">
              <div
                className="barAnim"
                id="intelBarChar1"
                style={{ width: `${this.state.intel}%` }}
              />
            </div>
          </div>
        </div>

        {this.renderMiniChars()}
      </div>
    );
  }
}

export default PlayerInfos;
