import React from 'react';

import '../../styles/css/fightZone/FightZone.css';
import Tap from './Tap';
import DisplayCountdown from './DisplayCountdown';
import mapFight from '../../styles/assets/newyorkFit.jpg';
import ModaleInstructions from './ModaleInstructions';

class FightZone extends React.Component {
  constructor() {
    super();
    this.state = {
      modaleIsOpen: false,
      counter: 2,
      winP1: false,
      winP2: false
    };
    this.handleCloseModal = this.handleCloseModal.bind(this);
    this.handleSetTimeOut = this.handleSetTimeOut.bind(this);
    // this.handleWinnerLeft = this.handleWinnerLeft.bind(this);
  }

  // componentDidMount() {
  //   console.log(this.state.counter);

  //   if (this.state.counter === 0) {
  //     this.handleWinner();
  //   }
  // }

  componentDidUpdate() {
    const { winP1, counter } = this.state;
    if (counter === 0 && !winP1) {
      this.handleWinnerLeft();
    }
  }

  // componentDidUpdate(prevprops) {
  //   console.log(this.state.counter);
  //   if (prevprops.counter === 0 /*&&  */) {
  //     this.handleWinnerLeft();
  //   } else if (prevprops.counter === 0 /*&&  */) {
  //     this.handleWinnerRight();
  //   }
  // }

  handleWinnerLeft = () => {
    this.setState({ winP1: true });
  };

  handleWinnerRight = () => {
    this.setState({ winP2: true });
  };

  handleCloseModal = () => {
    this.setState({ modaleIsOpen: !this.state.modaleIsOpen });
  };

  handleSetTimeOut = () => {
    const x = this;
    const { counter } = this.state;
    setTimeout(function() {
      if (counter > 0 && counter !== 0) {
        x.setState({ counter: counter - 1 });
      }
    }, 1000);
  };

  render() {
    const { modaleIsOpen, counter, winP1 } = this.state;
    const P1 = localStorage.getItem('avatarP1');
    const P2 = localStorage.getItem('avatarP2');
    return (
      <div className="mapFond">
        <div id={winP1 ? 'position1Win' : 'position2'}>
          <div className="winnerIs">
            <p>YOU WIN</p>
          </div>
        </div>
        <img className="fightMap" src={mapFight} alt="" />
        <DisplayCountdown
          counter={counter}
          setTimeOut={this.handleSetTimeOut}
          modaleIsOpen={modaleIsOpen}
        />
        <ModaleInstructions closeModal={this.handleCloseModal} modaleIsOpen={modaleIsOpen} />
        <div className="fighters">
          <div className="leftPlayer">
            <Tap />
            <img className="fighter1" src={P1} alt="harley" />
          </div>
          <div className="rightPlayer">
            <Tap />
            <img className="fighter2" src={P2} alt="deadpool" />
          </div>
        </div>
      </div>
    );
  }
}

export default FightZone;
