import React from 'react';

import ModaleInstructions from './ModaleInstructions';

class Countdown extends React.Component {
  constructor(props) {
    super(props);
    this.state = { counter: props.val, modaleIsOpen: false };

    this.closeModal = this.closeModal.bind(this);
  }

  closeModal = () => {
    this.setState({ modaleIsOpen: !this.state.modaleIsOpen });
  };

  render() {
    var x = this;
    var { counter } = this.state;
    setTimeout(function() {
      if (counter > 0) {
        x.setState({ counter: counter - 1 });
      }
    }, 1000);
    return <div className="count-button">{counter}</div>;
  }
}

export default Countdown;
