import React from 'react';
import mapFighter from '../../styles/assets/MAP2.png';
import '../../styles/css/MapButton.css';
import selectMap from '../../styles/assets/selectMap.gif';
import PlayersSelection from './PlayersSelection';

const pictureCity = [
  {
    flag: 'https://files.slack.com/files-pri/T6SG2QGG2-FQ5JDJLUX/newyorkfit.jpg'
  },
  {
    flag: 'https://files.slack.com/files-pri/T6SG2QGG2-FQ5USERK8/pekinfit.jpg'
  },
  {
    flag: 'https://files.slack.com/files-pri/T6SG2QGG2-FQ3NLL5EU/oslofit.jpg'
  },
  {
    flag: 'https://files.slack.com/files-pri/T6SG2QGG2-FQ3NV0R1T/saharafit.jpg'
  },
  {
    flag: 'https://files.slack.com/files-pri/T6SG2QGG2-FPQUXRJ8J/riofit.jpg'
  },
  {
    flag: 'https://files.slack.com/files-pri/T6SG2QGG2-FQ3NLL5C0/bombayfit.jpg'
  }
];
const cityButton = [
  {
    id: 0,
    button: '',
    country: [0],
    topVal: '37.1%',
    leftVal: '32.5%',
    bg: 'yellow',
    br: '20px',
    position: 'absolute'
  },
  {
    id: 1,
    button: '',
    country: [1],
    topVal: '35.4%',
    leftVal: '74.5%',
    bg: 'yellow',
    br: '20px',
    position: 'absolute'
  },
  {
    id: 2,
    button: '',
    country: [2],
    topVal: '22.4%',
    leftVal: '51.3%',
    bg: 'yellow',
    br: '20px',
    position: 'absolute'
  },
  {
    id: 3,
    button: '',
    country: [3],
    topVal: '44.9%',
    leftVal: '45.4%',
    bg: 'yellow',
    br: '20px',
    position: 'absolute'
  },
  {
    id: 4,
    button: '',
    country: [4],
    topVal: '73.8%',
    leftVal: '37.1%',
    bg: 'yellow',
    br: '20px',
    position: 'absolute'
  },
  {
    id: 5,
    button: '',
    country: [5],
    topVal: '45.5%',
    leftVal: '64.6%',
    bg: 'yellow',
    br: '20px',
    position: 'absolute'
  }
];
class MapButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      label: 0,
      button: false
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleClick2 = this.handleClick2.bind(this);
  }

  handleClick(e) {
    this.setState({
      label: e.target.value
    });
  }

  handleClick2(e) {
    this.setState({
      button: !this.state.button
    });
  }

  render() {
    return (
      <div className="mapButtonSelection">
        <div className="mapSelection">
          <img className="map" id="map1" src={mapFighter} alt="map" />
        </div>
        <div selMap>{this.state.button ? <selectMap /> : <PlayersSelection />}</div>
        <div className="citiesMap">
          {cityButton.map(x => (
            <button
              style={{
                top: x.topVal,
                left: x.leftVal,
                background: x.bg,
                'border-radius': x.br,
                position: x.position
              }}
              value={x.id}
              onMouseOver={this.handleClick}
            >
              {/* {x.button}  */}
            </button>
          ))}
        </div>
        <div className="flag-line">
          <div>
            {cityButton[this.state.label].country.map(x => (
              <img className="single-flag" src={pictureCity[x].flag} width="300px" alt={x.id} />
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default MapButton;
