  //     this.CoupDePied2();
  //     this.setState({ PV1: Math.round(this.state.PV1 - this.state.kick) });
  //   }
  // };

  fight = coup => {
    const pied1 = localStorage.getItem('strengthP1');
    const poing1 = localStorage.getItem('strengthP1');
    const pied2 = localStorage.getItem('strengthP2');
    const poing2 = localStorage.getItem('strengthP2');
    const def1 = localStorage.getItem('defP1');
    const def2 = localStorage.getItem('defP2');
    if (coup === 's') {
      this.setState({ kick: 10 * (1 + pied1 / 100) * ((100 - def2 / 2) / 100) });
      this.setState({ PV2: Math.round(this.state.PV2 - this.state.kick) });
    }
    if (coup === 'z') {
      this.setState({ punch: 5 * (1 + poing1 / 100) * ((100 - def2 / 2) / 100) });
      this.setState({ PV2: Math.round(this.state.PV2 - this.state.punch) });
    }
    if (coup === 'k') {
      this.setState({ kick: 10 * (1 + pied2 / 100) * ((100 - def1 / 2) / 100) });
      this.setState({ PV1: Math.round(this.state.PV1 - this.state.punch) });
    }
    if (coup === 'o') {
      this.setState({ punch: 5 * (1 + poing2 / 100) * ((100 - def1 / 2) / 100) });
      this.setState({ PV1: Math.round(this.state.PV1 - this.state.kick) });
    }
  };

  // CoupDePied1 = () => {
