  //   const def1 = localStorage.getItem('defP1');
  //   this.setState({ punch: 5 * (1 + poing2 / 100) * ((100 - def1 / 2) / 100) });
  // };

  // winner1 = () => {
  //   const { winP1, winP2, PV1, PV2 } = this.state;
  //   if (PV1 <= 0) {
  //     this.setState({ winP2: !winP2 });
  //   } else if (PV2 <= 0) {
  //     this.setState({ winP1: !winP1 });
  //   } else if () {

  //   }
  //   else {

