import React from 'react';
import { Link } from 'react-router-dom';

import '../../styles/css/selectMap&Char/FightButton.css';

class FightButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  handleReadyToFight = () => {
    if (this.props.bothReady === false) {
      return <button className="fight">FIGHT</button>;
    } else {
      return (
        <Link to="/fightZone/FightZone">
          <button className="fight ready">FIGHT</button>
        </Link>
      );
    }
  };

  render() {
    return <div className="button_fight">{this.handleReadyToFight()}</div>;
  }
}

export default FightButton;
