import React, { Component } from 'react';
import Versus from '../../styles/assets/versus.gif';
import PlayerInfos from './PlayerInfos';
import FightButton from './FightButton';
import '../../styles/css/PlayersSelection.css';
import axios from 'axios';

const CharSelection = [70, 280, 489, 18];

class PlayersSelection extends Component {
  constructor(props) {
    super(props);

    this.state = {
      selectedP1: false,
      selectedP2: false,
      apiPlayers: [],

      mapSelected: null
    };
  }

  componentDidMount() {
    this.getPlayers();
  }

  getPlayers = () => {
    CharSelection.forEach(bouillette => {
      return axios
        .get(`https://www.superheroapi.com/api.php/10156616570523372/${bouillette}`)
        .then(response => {
          this.state.apiPlayers.push(response.data);
          this.setState({ apiPlayers: this.state.apiPlayers });
        });
    });
  };

  render() {
    return (
      <div>
        <div className="charsSelection">
          <div id="player1">
            <PlayerInfos defPlayers={this.state.apiPlayers} />
          </div>

          <div className="between_Players">
            <div id="vsLocation">
              <img src={Versus} alt="VS" id="vsImage" />
            </div>
            <FightButton />
          </div>
          <div id="player2">
            <PlayerInfos defPlayers={this.state.apiPlayers} reverse={true} />
          </div>
        </div>
      </div>
    );
  }
}

export default PlayersSelection;
