import React from 'react';

import '../../styles/css/fightZone/DisplayCountdown.css';

class Countdown extends React.Component {
  constructor(props) {
    super(props);
    this.state = { counter: props.val };

    // this.handleSetTimeOut = this.handleSetTimeOut.bind(this);
  }
  // componentDidMount() {
  //   if (!this.props.modaleIsOpen) {
  //     console.log('didmount');
  //     this.props.setTimeOut();
  //   }
  // }

  render() {
    setTimeout(function() {
      if (this.state.counter > 0) {
        this.setState({ counter: this.state.counter - 1 });
      }
    }, 1000);
    return <div className="count-button">{this.state.counter}</div>;
  }
}

export default Countdown;

