import React, { Component } from 'react';
import axios from 'axios';

import CheatPlayer from './CheatPlayer';
import '../../styles/css/PlayerInfos.css';

class PlayerInfos extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cheatCount: 0,

      name: 'Oups',
      strength: 50,
      intel: 70,
      avatar: 'https://pbs.twimg.com/media/C8SFjSbXgAAKoZx.jpg:large'
    };
  }

  getCharacter() {
    axios
      .get(`https://www.superheroapi.com/api.php/10156616570523372`)
      .then(response => console.log(response.data));
  }

  onClickHandler = () => {
    this.setState({
      cheatCount: this.state.cheatCount + 1
    });
  };

  renderMiniDefault() {
    return (
      <div>
        <img className="miniPic" src="https://via.placeholder.com/150" alt="placeholder" />
        <img className="miniPic" src="https://via.placeholder.com/150" alt="placeholder" />
        <img className="miniPic" src="https://via.placeholder.com/150" alt="placeholder" />
        <img className="miniPic" src="https://via.placeholder.com/150" alt="placeholder" />
      </div>
    );
  }

  renderMiniCheat() {
    return (
      <div>
        <CheatPlayer />
      </div>
    );
  }

  renderMiniChars() {
    let clickAvatar = this.state.cheatCount;
    if (clickAvatar !== 0 && clickAvatar % 5 === 0) {
      return this.renderMiniCheat();
    } else {
      return this.renderMiniDefault();
    }
  }

  render() {
    return (
      <div>
        <div className="selectedChar">
          <img
            src={this.state.avatar}
            className="charPics"
            onClick={() => this.onClickHandler()}
            alt="Portrait"
          />
          <div className="charInfos">
            <p className="nameChar">{this.state.name}</p>
            <p>Strength</p>
            <div className="emptyStatBar">
              <div
                className="barAnim"
                id="strengthBarChar"
                style={{ width: `${this.state.strength}%` }}
              />
            </div>
            <p>Intelligence</p>
            <div className="emptyStatBar">
              <div
                className="barAnim"
                id="intelBarChar"
                style={{ width: `${this.state.intel}%` }}
              />
            </div>
          </div>
        </div>

        {this.renderMiniChars()}
        {this.getCharacter()}
      </div>
    );
  }
}

export default PlayerInfos;
