import React from 'react';
import '../../styles/css/fightZone/Tap.css';

class Tap extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  startfightMobile = () => {
    const { close } = this.state;
    if (close === true) {
      return;
    }
    if (!close) {
      this.props.fight();
    }
  };

  render() {
    return (
      <button className="tap" onClick={() => this.props.fight()}>
        Tap
      </button>
    );
  }
}

export default Tap;
