import React, { Component } from 'react';

import '../../styles/css/fightZone/ModaleInstructions.css';
import rouleau from '../../styles/assets/rouleau.png';

class ModaleInstructions extends Component {
  render() {
    console.log(this.props);
    return (
      <div className={this.props.modaleIsOpen ? 'modale_close' : 'modale_open'}>
        <img
          className={this.props.modaleIsOpen ? 'rouleau_close' : 'rouleau'}
          src={rouleau}
          alt="parchemin"
        />
        <div className={this.props.modaleIsOpen ? 'instructions_close' : 'instructions'}>
          <div>
            <h2>How to play :</h2>
          </div>
          <ul>
            <p>Player One clicks on Tap</p>
            <p>Player Two clicks on Tap</p>
            <p>Results of the assault</p>
            <p>And start over</p>
            <p>Have a g'O_O'd game</p>
          </ul>
          <button onClick={() => this.props.closeModal()}>Play now</button>
        </div>
      </div>
    );
  }
}

export default ModaleInstructions;
