import React from 'react';
// import Link from 'react-router-dom';

import selectMap from '../../styles/assets/selectMap.gif';
import Link from 'react-router-dom';
import mapFighter from '../../styles/assets/MAP2.png';
import PlayersSelection from './PlayersSelection';
import '../../styles/css/MapButton.css';

class MapButton extends React.Component {
  constructor() {
    super();
    this.state = {
      button1: true,
      button2: true,
      button3: true,
      button4: true,
      button5: true,
      button6: true
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleClick2 = this.handleClick2.bind(this);
    this.handleClick3 = this.handleClick3.bind(this);
    this.handleClick4 = this.handleClick4.bind(this);
    this.handleClick5 = this.handleClick5.bind(this);
    this.handleClick6 = this.handleClick6.bind(this);
  }
  handleClick() {
    this.setState({
      button1: !this.state.button1
    });
  }
  handleClick2() {
    this.setState({
      button2: !this.state.button2
    });
  }
  handleClick3() {
    this.setState({
      button3: !this.state.button3
    });
  }
  handleClick4() {
    this.setState({
      button4: !this.state.button4
    });
  }
  handleClick5() {
    this.setState({
      button5: !this.state.button5
    });
  }
  handleClick6() {
    this.setState({
      button6: !this.state.button6
    });
  }

  render() {
    return (
      <div>
        <div className="mapButtonSelection">
          <div className="mapSelection">
            <img className="map" id="map1" src={mapFighter} />
            <div className="mapButton">
              <button
                className={this.state.button1 ? 'nyc' : 'nycV'}
                onClick={this.handleClick}
                class="nyc"
              ></button>
              <button
                className={this.state.button2 ? 'pekin' : 'pekinV'}
                onClick={this.handleClick2}
                class="pekin"
              ></button>
              <button
                className={this.state.button3 ? 'bombay' : 'bombayV'}
                onClick={this.handleClick3}
                class="bombay"
              ></button>
              <button
                className={this.state.button4 ? 'sahara' : 'saharaV'}
                onClick={this.handleClick4}
                class="sahara"
              ></button>
              <button
                className={this.state.button5 ? 'oslo' : 'osloV'}
                onClick={this.handleClick5}
                class="oslo"
              ></button>
              <button
                className={this.state.button6 ? 'rio' : 'rioV'}
                onClick={this.handleClick6}
                class="rio"
              ></button>
              <div className="selectMap">
                <img src={selectMap} alt="SM" id="selectMap" />
              </div>
            </div>
          </div>
        </div>
        <div className="charMap1">
          {this.state.button1 ? '' : <PlayersSelection />}
          {this.state.button2 ? '' : <PlayersSelection />}
          {this.state.button3 ? '' : <PlayersSelection />}
          {this.state.button4 ? '' : <PlayersSelection />}
          {this.state.button5 ? '' : <PlayersSelection />}
          {this.state.button6 ? '' : <PlayersSelection />}
        </div>
      </div>
    );
  }
}

export default MapButton;
