import React, { Component } from 'react';

import '../../styles/css/fightZone/ModaleInstructions.css';
import rouleau from '../../styles/assets/rouleau.png';
import Tap from './Tap';

class ModaleInstructions extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modaleIsOpen: false
    };
    this.closeModal = this.closeModal.bind(this);
  }

  closeModal = () => {
    this.setState({ modaleIsOpen: !this.state.modaleIsOpen });
  };

  render() {
    return (
      <div className={this.state.modaleIsOpen ? 'modale_close' : 'modale_open'}>
        <img
          className={this.state.modaleIsOpen ? 'modale_close' : 'rouleau'}
          src={rouleau}
          alt="parchemin"
        />
        <div className="instructions">
          <div>
            <h2>How to play :</h2>
          </div>
          <ul>
            <p>Player One clicks on Tap</p>
            <p>Player Two clicks on Tap</p>
            <p>Results of the assault</p>
            <p>And start over</p>
            <p>Have a g'O_O'd game</p>
          </ul>
          <button onClick={() => this.closeModal()}>Play now</button>
        </div>
      </div>
    );
  }
}

export default ModaleInstructions;

