import React from 'react';
import axios from 'axios';

const CharSelection = [70, 140, 210, 280];

let ApiObjects = [];

const GetCharacter = cochonou =>
  axios
    .get(`https://www.superheroapi.com/api.php/10156616570523372/${cochonou}`)
    .then(response => response.data)
    .then(data => {
      ApiObjects.push(data);
    });

function ApiChars() {
  CharSelection.map(bouillette => GetCharacter(bouillette));
  console.log('apiobject', ApiObjects);
  return ApiObjects.map(tsouintsouin => (
    <img
      className="miniPic"
      src={tsouintsouin.image.url}
      alt={tsouintsouin.name}
      onMouseOver={() => console.log(tsouintsouin.name)}
    />
  ));
}

export default ApiChars;
