  handleSetTimeOut = () => {
    console.log('ok1');
    setTimeout(function() {
      console.log('ok2');
      if (this.state.counter > 0 /*&& !modaleIsOpen*/) {
        this.State({ counter: this.state.counter - 1 });
      }
    }, 1000);
  };

  render() {
    const { setTimeOut, counter, modaleIsOpen } = this.props;
    return <div className="count-button">{counter}</div>;
  }
}

export default Countdown;
